import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';
import StarRatings from 'react-star-ratings';
import { AnimationWrapper } from 'react-hover-animation';

class ReviewsSlider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cols: 4,
      step1: true,
      step2: false,
    };
    this.updateWindowSize.bind(this);
  }

  updateWindowSize = () => {
    if (window.outerWidth >= 1230) {
      this.setState({ itemCount: 3, cols: 4 });
    } else if (window.outerWidth >= 970 && window.outerWidth < 1230) {
      this.setState({ itemCount: 3, cols: 4 });
    } else if (window.outerWidth <= 970) {
      this.setState({ itemCount: 1, cols: 12 });
    }
  };

  componentDidMount() {
    window.addEventListener('resize', this.updateWindowSize);
    this.updateWindowSize();
  }

  // Make sure to remove the DOM listener when the component is unmounted.
  componentWillUnmount() {
    window.removeEventListener('resize', this.updateWindowSize);
  }
  render() {
    return (
      <React.Fragment>
        <Row className="justify-content-center">
          <Col lg="12" className="mt-1">
            <div id="customer-testi" className="tiny-three-item">
              <div className="tiny-slide">
                <Row>
                  {this.state.step1 === true
                    ? this.props.reviews.map((review, key) =>
                        key >= 0 && key <= 2 ? (
                          <AnimationWrapper>
                            <Col
                              key={key}
                              md={this.state.cols}
                              className="mb-1"
                            >
                              <div
                                className="d-flex client-testi"
                                name="clientsreview"
                              >
                                {/* <img
                                src={review.img}
                                style={{ height: 65, width: 65 }}
                                className="avatar avatar-small client-image rounded shadow"
                                alt=""
                              /> */}
                                <div className="flex-1 content p-3 shadow-lg rounded bg-white position-relative">
                                  <StarRatings
                                    items={3}
                                    rating={review.rating}
                                    starRatedColor="#F17425"
                                    numberOfStars={5}
                                    name="rating"
                                    starDimension="15px"
                                    starSpacing="3px"
                                  />
                                  <p className="text-muted mt-2">
                                    " {review.desc} "
                                  </p>
                                  <h6 className="text-primary">
                                    - {review.name} <br />
                                    <small className="text-muted">
                                      {review.post}
                                    </small>
                                  </h6>
                                </div>
                              </div>
                            </Col>
                          </AnimationWrapper>
                        ) : null,
                      )
                    : this.props.reviews.map((review, key) =>
                        key >= 3 && key <= 5 ? (
                          <AnimationWrapper key={key}>
                            <Col md={this.state.cols} className="mb-1">
                              <div
                                className="d-flex client-testi"
                                name="clientsreview"
                              >
                                <img
                                  src={review.img}
                                  style={{ height: 65, width: 65 }}
                                  className="avatar avatar-small client-image rounded shadow"
                                  alt=""
                                />
                                <div className="flex-1 content p-3 shadow rounded bg-white position-relative">
                                  <StarRatings
                                    items={3}
                                    rating={review.rating}
                                    starRatedColor="#F17425"
                                    numberOfStars={5}
                                    name="rating"
                                    starDimension="15px"
                                    starSpacing="3px"
                                    className="mb-0"
                                  />
                                  <p className="text-muted mt-2">
                                    " {review.desc} "
                                  </p>
                                  <h6 className="text-primary">
                                    - {review.name}{' '}
                                    <small className="text-muted">
                                      {review.post}
                                    </small>
                                  </h6>
                                </div>
                              </div>
                            </Col>
                          </AnimationWrapper>
                        ) : null,
                      )}
                </Row>
              </div>
            </div>
            {/* <div className="tns-nav">
              <button
                type="button"
                onClick={() => {
                  this.setState({ step1: true, step2: false });
                }}
                className={
                  this.state.step1 ? "tns-nav-active" : "tns-nav-inactive"
                }
              ></button>
              <button
                type="button"
                onClick={() => {
                  this.setState({ step1: false, step2: true });
                }}
                className={
                  this.state.step2 ? "tns-nav-active" : "tns-nav-inactive"
                }
              ></button>
            </div> */}
          </Col>
        </Row>
      </React.Fragment>
    );
  }
}

export default ReviewsSlider;
