import React, { Component, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Col,
  Container,
  Row,
  Form,
  Button,
  FormGroup,
  Input,
  FormFeedback,
  Label,
  Card,
  CardBody,
  Alert,
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption,
} from 'reactstrap';

//import bg image
// import bg_image from '../../assets/images/construction/bg.jpg';
import bg_image from '../../assets/images/construction/bg5.png';
import FeatherIcon from 'feather-icons-react';
import emailjs, { send } from '@emailjs/browser';
import Typist from 'react-typist';

const Section = () => {
  const [succeMsg, setsucceMsg] = useState(false);

  const validation = useFormik({
    enableReinitialize: true,

    initialValues: {
      name: '',
      email: '',
    },
    validationSchema: Yup.object({
      name: Yup.string().required('Please Enter Name'),
      email: Yup.string().required('Email is required'),
    }),
    onSubmit: (values) => {
      setsucceMsg(true);
      // console.log(values)
    },
  });

  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        'service_3tekrls',
        'template_3j9l639',
        form.current,
        'WJW0NM0MduGhIRYE8',
      )
      .then(
        (result) => {
          setName('');
          setEmail('');
          setPhone('');
          setCompany('');
          setTime('');
          setsucceMsg(true);
        },
        (error) => {
          console.log(error.text);
          alert('FAILED...', error);
        },
      );
  };

  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [phone, setPhone] = useState();
  const [company, setCompany] = useState();
  const [time, setTime] = useState();
  const [show, setShow] = useState(false);

  //format phone number
  const formatPhone = (e) => {
    const value = e.target.value;
    const newValue = value.replace(/\D/g, '');
    const newValue2 = newValue.replace(/^(\d{3})(\d)/, '($1) $2');
    const newValue3 = newValue2.slice(0, 13);
    const newValue4 = newValue3.replace(/(\d)(\d{4})$/, '$1-$2');
    setPhone(newValue4);
  };

  //carousel
  // State for Active index
  const [activeIndex, setActiveIndex] = React.useState(0);

  // State for Animation
  const [animating, setAnimating] = React.useState(false);

  // Sample items for Carousel
  const items = [
    {
      caption: 'Sample Caption One',
      src: 'https://media.geeksforgeeks.org/wp-content/uploads/20210425122739/2-300x115.png',
      altText: 'Slide One',
    },
    {
      caption: 'Sample Caption Two',
      src: 'https://media.geeksforgeeks.org/wp-content/uploads/20210425122716/1-300x115.png',
      altText: 'Slide Two',
    },
  ];

  // Items array length
  const itemLength = items.length - 1;

  // Previous button for Carousel
  const previousButton = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? itemLength : activeIndex - 1;
    setActiveIndex(nextIndex);
  };

  // Next button for Carousel
  const nextButton = () => {
    if (animating) return;
    const nextIndex = activeIndex === itemLength ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };

  const carouselItemData = items.map((item) => {
    return (
      <CarouselItem
        key={item.src}
        onExited={() => setAnimating(false)}
        onExiting={() => setAnimating(true)}
      >
        <img src={item.src} alt={item.altText} />
      </CarouselItem>
    );
  });

  return (
    <React.Fragment>
      {/* <div
          style={{
            display: 'block',
            width: 320,
            padding: 30,
          }}
        >
          <h8>ReactJS Reactstrap Carousel Component</h8>
          <Carousel
            previous={previousButton}
            next={nextButton}
            activeIndex={activeIndex}
          >
            <CarouselIndicators
              items={items}
              activeIndex={activeIndex}
              onClickHandler={(newIndex) => {
                if (animating) return;
                setActiveIndex(newIndex);
              }}
            />
            {carouselItemData}
            <CarouselControl
              directionText="Prev"
              direction="prev"
              onClickHandler={previousButton}
            />
            <CarouselControl
              directionText="Next"
              direction="next"
              onClickHandler={nextButton}
            />
          </Carousel>
        </div> */}
      <section
        className="bg-half-260 d-table w-100"
        style={{ background: `url(${bg_image}) center center` }}
        id="home"
      >
        <div className="bg-overlay" style={{ opacity: '0.65' }}></div>
        <Container>
          <Row className="mt-5 mt-md-4">
            <Col className="d-flex align-items-center">
              <div
                className="title-heading p-3"
                // style={{ backgroundColor: 'rgba(0, 0, 0, 0.5)' }}
              >
                <h1 className="display-3 fw-bold mb-3">
                  <span style={{ color: '#9dce66' }}>Experience</span> <br />
                  <Typist>
                    <span className="text-white element">Better Lighting</span>
                    <Typist.Backspace count={15} delay={300} />
                    <span className="text-white element">Energy Savings</span>
                    <Typist.Backspace count={15} delay={500} />
                    <span className="text-white element">
                      a Brighter Future
                    </span>
                  </Typist>
                </h1>
                <p className="para-desc text-white" style={{ opacity: '75%' }}>
                  Get a customized LED lighting plan for your business, inside
                  and out.
                </p>
              </div>
              {/* <div
                className="title-heading p-3"
                // style={{ backgroundColor: 'rgba(0, 0, 0, 0.5)' }}
              >
                <h4 className="display-4 mb-4 fw-bold text-white title-dark">
                  Experience <br /> a brighter future
                </h4>
                <p className="para-desc text-white" style={{ opacity: '75%' }}>
                  Get a customized LED lighting plan for your business, inside
                  and out.
                </p> */}
              {/* <div className="mt-4 pt-2">
                  <Link to="#" className="btn btn-primary">
                    Get Started
                  </Link>
                </div> */}
              {/* </div> */}
            </Col>
            <Col
              lg="5"
              md={{ size: 6, order: 1 }}
              xs={{ order: 2 }}
              className=""
            >
              {!succeMsg ? (
                <Card className="rounded border-0 shadow-lg">
                  <CardBody>
                    <div className="pb-3">
                      <h4 className="card-title fw-bold">
                        Get a free lighting assessment
                      </h4>
                      <p className="text-muted">
                        Find out how your business can save up to 50% or more on
                        your electric bill and maintenance costs.{' '}
                      </p>
                    </div>
                    <div className="custom-form bg-white">
                      <div id="message"></div>
                      {/* <Alert
                        color="info"
                        isOpen={succeMsg}
                        toggle={() => {
                          setsucceMsg(false);
                        }}
                      >
                        We received your inquiry and will get back to you soon!
                      </Alert> */}
                      <form
                        onSubmit={sendEmail}
                        name="contact-form"
                        id="contact-form"
                        ref={form}
                      >
                        <Row>
                          <Col lg="6">
                            <FormGroup className="mb-3">
                              <div className="form-icon position-relative">
                                <i>
                                  <FeatherIcon
                                    icon="user"
                                    className="fea icon-sm icons"
                                  />
                                </i>
                              </div>
                              <Input
                                className="form-control ps-5"
                                placeholder="Name :"
                                type="name"
                                name="name"
                                id="name"
                                value={name}
                                required
                              />
                              {validation.touched.name &&
                              validation.errors.name ? (
                                <FormFeedback type="invalid">
                                  {validation.errors.name}
                                </FormFeedback>
                              ) : null}
                            </FormGroup>
                          </Col>
                          <Col lg="6">
                            <FormGroup className="mb-3">
                              <div className="form-icon position-relative">
                                <i>
                                  <FeatherIcon
                                    icon="mail"
                                    className="fea icon-sm icons"
                                  />
                                </i>
                              </div>
                              <Input
                                className="form-control ps-5"
                                placeholder="Your email :"
                                type="email"
                                name="email"
                                id="email"
                                value={email}
                                required
                              />
                              {validation.touched.email &&
                              validation.errors.email ? (
                                <FormFeedback type="invalid">
                                  {validation.errors.email}
                                </FormFeedback>
                              ) : null}
                            </FormGroup>
                          </Col>
                          <Col>
                            <FormGroup className="mb-3">
                              <div className="form-icon position-relative">
                                <i>
                                  <FeatherIcon
                                    icon="briefcase"
                                    className="fea icon-sm icons"
                                  />
                                </i>
                              </div>
                              <Input
                                className="form-control ps-5"
                                placeholder="Company :"
                                type="text"
                                name="company"
                                id="company"
                                value={company}
                                required
                              />
                              {validation.touched.name &&
                              validation.errors.name ? (
                                <FormFeedback type="invalid">
                                  {validation.errors.name}
                                </FormFeedback>
                              ) : null}
                            </FormGroup>
                          </Col>
                          <Col>
                            <FormGroup className="mb-3">
                              <div className="form-icon position-relative">
                                <i>
                                  <FeatherIcon
                                    icon="phone"
                                    className="fea icon-sm icons"
                                  />
                                </i>
                              </div>
                              <Input
                                className="form-control ps-5"
                                placeholder="Phone :"
                                type="tel"
                                onChange={formatPhone}
                                name="phone"
                                id="phone"
                                value={phone}
                                required
                              />
                              {validation.touched.name &&
                              validation.errors.name ? (
                                <FormFeedback type="invalid">
                                  {validation.errors.name}
                                </FormFeedback>
                              ) : null}
                            </FormGroup>
                          </Col>
                        </Row>
                        <Row>
                          <Col sm="12" className="text-center">
                            <div className="d-grid">
                              <input
                                type="submit"
                                id="submit"
                                name="send"
                                className="submitBnt btn btn-primary"
                                value="Get Free Assessment"
                              />
                              <div id="simple-msg"></div>
                            </div>
                          </Col>
                        </Row>
                      </form>
                    </div>
                  </CardBody>
                </Card>
              ) : (
                <Card>
                  <CardBody>
                    <h6>
                      We received your message and will get back to you{' '}
                      <span className="text-primary">shortly</span>!
                    </h6>
                  </CardBody>
                </Card>
              )}
            </Col>
          </Row>
        </Container>
      </section>
    </React.Fragment>
  );
};

export default Section;
