import React, { Component, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Col,
  Container,
  Row,
  Form,
  Button,
  FormGroup,
  Input,
  FormFeedback,
  Label,
  Card,
  CardBody,
  Alert,
} from 'reactstrap';

//import bg image
import bg_image from '../../assets/images/construction/bg.jpg';
import FeatherIcon from 'feather-icons-react';

const Section = () => {
  const [succeMsg, setsucceMsg] = useState(false);

  const validation = useFormik({
    enableReinitialize: true,

    initialValues: {
      name: '',
      email: '',
    },
    validationSchema: Yup.object({
      name: Yup.string().required('Please Enter Name'),
      email: Yup.string().required('Email is required'),
    }),
    onSubmit: (values) => {
      setsucceMsg(true);
      // console.log(values)
    },
  });

  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        'service_8dmdsi7',
        'template_th7bztm',
        form.current,
        'WJW0NM0MduGhIRYE8',
      )
      .then(
        (result) => {
          setName('');
          setEmail('');
          setPhone('');
          setCompany('');
          setTime('');
          setsucceMsg(true);
        },
        (error) => {
          console.log(error.text);
          alert('FAILED...', error);
        },
      );
  };

  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [phone, setPhone] = useState();
  const [company, setCompany] = useState();
  const [time, setTime] = useState();
  const [show, setShow] = useState(false);

  //format phone number
  const formatPhone = (e) => {
    const value = e.target.value;
    const newValue = value.replace(/\D/g, '');
    const newValue2 = newValue.replace(/^(\d{3})(\d)/, '($1) $2');
    const newValue3 = newValue2.slice(0, 13);
    const newValue4 = newValue3.replace(/(\d)(\d{4})$/, '$1-$2');
    setPhone(newValue4);
  };
  return (
    <React.Fragment>
      <section
        className="bg-half-260 d-table w-100"
        style={{ background: `url(${bg_image}) center center` }}
        id="home"
      >
        <div className="bg-overlay" style={{ opacity: '0.65' }}></div>
        <Container>
          <Row className="mt-5 mt-md-4">
            <Col>
              <div className="title-heading">
                <h4 className="display-4 mb-4 fw-bold text-white title-dark">
                  Experience <br /> a brighter future
                </h4>
                <p className="para-desc text-white-50">
                  Get a customized LED lighting plan for your business, inside
                  and out.
                </p>
                <div className="mt-4 pt-2">
                  <Link to="#" className="btn btn-primary">
                    Get Started
                  </Link>
                </div>
              </div>
            </Col>
            <Col>
              <h1>
                <Card className="bg-light"></Card>
              </h1>
            </Col>
          </Row>
        </Container>
      </section>
    </React.Fragment>
  );
};

export default Section;
